mapper.select:
SELECT CASE WHEN COUNT(*) > 0 THEN 1 ELSE 0 END
FROM USERS 
WHERE USER_ID = #{userId}

mapper.update:
UPDATE USERS
SET PASSWORD = #{password}, NICKNAME = #{nickname}, EMAIL = #{email}
WHERE PK_USER = #{pkUser}

UPDATE USERS
SET PASSWORD = #{password}
WHERE PK_USER = #{pkUser}

UPDATE USERS
SET NICKNAME = #{nickname}
WHERE PK_USER = #{pkUser}
